
public class operators1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int a=45;
        //post increment-----a++(45)
        // pre increment----++a(47)
        
        System.out.println(a++);//45
        System.out.println(a);//46
        System.out.println(++a);//47
        
        
        
        
	}

}
